note_models.css:
.card {
 font-family: georgia;
 font-size: 19px;
 text-align: center;
 color: black;
 background-color: white;
 word-wrap: break-word
 /* background: url("image.png") repeat;  */
 margin: 0;
 margin: 1ex 0em;
}

.textstyling {
 margin: .75ex 1.15em
}

.mobile .night_mode .textstyling {
 margin: 1.5ex .75em;
 color: #00aaff !important;

}

img {
    display: block;
    margin: auto;
    width: 100%;
}

.card img {max-width: 50%; max-height: none;}
.mobile .card img {max-width: 100%; max-height: none;}
.mobile .card {margin: 0ex 0em;}
.mobile #content {margin: 0ex 0em;}

/* The glorious king wishes you the best as you prepare for boards! Stay strong my friends!*/


html {
    overflow: scroll;
    overflow-x: hidden;
}
::-webkit-scrollbar {
    width: 0px;  /* remove scrollbar space */
    /*background: transparent;   optional: just make scrollbar invisible */
}
/* optional: show position indicator in red */
::-webkit-scrollbar-thumb {
    background: #FF0000;
}

a {
  color: #2f4f4f;
  text-decoration: inherit;
  font-family: Times New Roman;
  font-size: 9px; 
  font-style: normal !important;
 font-weight: lighter !important;
}


body, div
{
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: -moz-none;
    -ms-user-select: none;
    -o-user-select: none;
    user-select: none;
}

.overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url("image.png") repeat;
}


img#star { max-width: 50px; }


.entiresketchstyle {
}

.card.night_mode {
 color: #0f0;
 background-color: black;
 font-weight: bold !important; 
 margin: 0;
 margin: .25ex 0em; 
}

.cloze.night_mode {
 color: #F4F406;<!--45b3e7;-->
}

.entiresketchstyle {
font-size: 12px;
 font-style: italic !important;
 font-weight: lighter !important;
}

